def start_tray():
    def stray_handler(_, item):
        if str(item) == 'Stream Webcam':
            mainService.open_stream('WEBCAM')
        elif str(item) == 'Stop Webcam':
            mainService.stop_thread()
        elif str(item) == 'Send ciao':
            websocket_manager.send_string('ciao!')
        elif str(item) == 'Exit':
            mainService.stop_thread()
            websocket_manager.ws.close()
            os._exit(1)

    pystray.Icon(
        'MrTcp',
        Image.open("icon.ico"),
        menu=pystray.Menu(
            pystray.MenuItem('Stream Webcam', stray_handler),
            pystray.MenuItem('Stop Webcam', stray_handler),
            pystray.Menu.SEPARATOR,
            pystray.MenuItem('Send ciao', stray_handler),
            pystray.Menu.SEPARATOR,
            pystray.MenuItem('Exit', stray_handler),
        )
    ).run()


if __name__ == '__main__':
    Thread(target=start_tray).start()
    websocket_manager.run()
